package sample;

public class StarExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0; i<5;i++)
		{
			for(int j=0; j<=i;j++)
			{
				System.out.print(" * ");
			}
			
			System.out.println();
			
		}//outer for
		
		
		
		System.out.println("########");
		
	}

}
